sys_script_include.script:
var TPSMIntegrationUtils = Class.create();
TPSMIntegrationUtils.prototype = {
    initialize: function() {
    },

	logRequest: function(payload, url, ip, integration, method) {
		var gr = new GlideRecord('x_77271_csm_integr_integration_staging');
		gr.initialize();
		gr.http_method = method;
		gr.integration = integration;
		gr.payload = payload;
		gr.source_ip = ip;
		gr.url = url;
		gr.status = 'pending';
		var sysId = gr.insert();

		return sysId;
	},

    type: 'TPSMIntegrationUtils'
};
